@st.cache_data
def load_data():
    """Load or generate sample data."""
    np.random.seed(42)
    dates = pd.date_range(start='2024-01-01', periods=365, freq='D')
    
    data = {
        'total_emission_kg_co2e': np.random.uniform(50, 150, 365),
        'electricity_emission': np.random.uniform(10, 40, 365),
        'vehicle_emission': np.random.uniform(5, 30, 365),
        'waste_emission': np.random.uniform(2, 15, 365),
        'water_emission': np.random.uniform(1, 10, 365),
        'diet_emission': np.random.uniform(5, 25, 365),
        'household_size': np.random.randint(1, 6, 365),
        'gsi_score': np.random.uniform(30, 85, 365),
    }
    
    df = pd.DataFrame(data, index=dates)
    return df

# ============================================================================
# CHART FUNCTIONS
# ============================================================================

def create_emission_chart(df):
    """Create professional emission timeline."""
    fig = go.Figure()
    
    fig.add_trace(go.Scatter(
        x=df.index,
        y=df['total_emission_kg_co2e'],
        mode='lines',
        name='Daily Emissions',
        line=dict(color='#10b981', width=2.5),
        fill='tozeroy',
        fillcolor='rgba(16, 185, 129, 0.15)',
        hovertemplate='<b>%{x|%b %d}</b><br>%{y:.1f} kg CO2e<extra></extra>'
    ))
    
    fig.update_layout(
        title={
            'text': 'Your Carbon Emissions Timeline',
            'font': {'size': 20, 'color': '#cbd5e1', 'family': 'Sora'},
            'x': 0.5,
            'xanchor': 'center'
        },
        xaxis_title='Date',
        yaxis_title='Emissions (kg CO2e)',
        hovermode='x unified',
        height=400,
        plot_bgcolor='#1a2341',
        paper_bgcolor='#0f172a',
        font=dict(color='#cbd5e1', family='Inter'),
        margin=dict(t=60, l=60, r=20, b=50),
        xaxis=dict(
            gridcolor='#334155',
            showgrid=True,
            color='#64748b'
        ),
        yaxis=dict(
            gridcolor='#334155',
            showgrid=True,
            color='#64748b'
        )
    )
    
    return fig

def create_breakdown_chart(emissions_dict):
    """Create professional pie chart."""
    colors = ['#10b981', '#14b8a6', '#06b6d4', '#0284c7', '#6366f1', '#ec4899']
    
    fig = go.Figure(data=[go.Pie(
        labels=list(emissions_dict.keys()),
        values=list(emissions_dict.values()),
        hole=0.35,
        marker=dict(
            colors=colors,
            line=dict(color='#0f172a', width=2)
        ),
        textposition='outside',
        textinfo='label+percent',
        hovertemplate='<b>%{label}</b><br>%{value:.1f} kg CO2e<extra></extra>',
        textfont=dict(color='#cbd5e1', family='Inter')
    )])
    
    fig.update_layout(
        title={
            'text': 'Emission Sources Breakdown',
            'font': {'size': 20, 'color': '#cbd5e1', 'family': 'Sora'},
            'x': 0.5,
            'xanchor': 'center'
        },
        height=400,
        plot_bgcolor='#1a2341',
        paper_bgcolor='#0f172a',
        font=dict(color='#cbd5e1', family='Inter'),
        margin=dict(t=60, l=20, r=20, b=20),
        showlegend=True,
        legend=dict(font=dict(color='#cbd5e1'))
    )
    
    return fig

def create_comparison_chart(categories, values):
    """Create comparison bar chart."""
    colors = ['#10b981', '#14b8a6', '#06b6d4', '#0284c7', '#6366f1']
    
    fig = go.Figure(data=[
        go.Bar(
            x=categories,
            y=values,
            marker=dict(
                color=colors[:len(categories)],
                line=dict(color='#cbd5e1', width=1)
            ),
            text=[f'{v:.1f}' for v in values],
            textposition='outside',
            hovertemplate='<b>%{x}</b><br>%{y:.1f} kg CO2e<extra></extra>'
        )
    ])
    
    fig.update_layout(
        title='Category Comparison',
        title_font=dict(size=18, color='#cbd5e1', family='Sora'),
        xaxis_title='Category',
        yaxis_title='Emissions (kg CO2e)',
        height=350,
        plot_bgcolor='#1a2341',
        paper_bgcolor='#0f172a',
        font=dict(color='#cbd5e1', family='Inter'),
        margin=dict(t=60, l=60, r=20, b=50),
        xaxis=dict(gridcolor='#334155', color='#64748b'),
        yaxis=dict(gridcolor='#334155', color='#64748b'),
        showlegend=False
    )
    
    return fig

