def show_dashboard(df):
    """Dashboard page."""
    
    # Metrics
    col1, col2, col3, col4 = st.columns(4, gap="medium")
    
    avg_daily = df['total_emission_kg_co2e'].mean()
    annual_total = avg_daily * 365
    earth_equiv = annual_total / (5.5 * 365)  # Global avg per capita
    gsi_score = df['gsi_score'].mean()
    
    with col1:
        st.markdown(f"""
        <div class="metric-card">
            <div class="metric-label">Avg Daily</div>
            <div class="metric-value">{avg_daily:.1f}</div>
            <div class="metric-unit">kg CO2e</div>
        </div>
        """, unsafe_allow_html=True)
    
    with col2:
        st.markdown(f"""
        <div class="metric-card">
            <div class="metric-label">Annual</div>
            <div class="metric-value">{annual_total:.0f}</div>
            <div class="metric-unit">kg CO2e/year</div>
        </div>
        """, unsafe_allow_html=True)
    
    with col3:
        st.markdown(f"""
        <div class="metric-card">
            <div class="metric-label">Earth Equiv.</div>
            <div class="metric-value">{earth_equiv:.2f}</div>
            <div class="metric-unit">Earths needed</div>
        </div>
        """, unsafe_allow_html=True)
    
    with col4:
        status = "Above Average" if earth_equiv > 1 else "Sustainable"
        color = "#ec4899" if earth_equiv > 1 else "#10b981"
        st.markdown(f"""
        <div class="metric-card">
            <div class="metric-label">Status</div>
            <div class="metric-value" style="font-size: 1.4rem; color: {color};">{status}</div>
            <div class="metric-unit">vs. Global</div>
        </div>
        """, unsafe_allow_html=True)
    
    st.markdown("---")
    
    # Charts in tabs
    tab1, tab2, tab3 = st.tabs(["Timeline", "Breakdown", "Comparison"])
    
    with tab1:
        st.plotly_chart(create_emission_chart(df.tail(90)), use_container_width=True, height=450)
    
    with tab2:
        col_left, col_right = st.columns([1.2, 1])
        
        with col_left:
            electricity = df['electricity_emission'].sum()
            vehicle = df['vehicle_emission'].sum()
            waste = df['waste_emission'].sum()
            water = df['water_emission'].sum()
            diet = df['diet_emission'].sum()
            
            emissions_dict = {
                'Energy': electricity,
                'Transport': vehicle,
                'Diet': diet,
                'Waste': waste,
                'Water': water,
            }
            
            st.plotly_chart(create_breakdown_chart(emissions_dict), use_container_width=True, height=450)
        
        with col_right:
            st.markdown('<h3 class="card-title">Source Details</h3>', unsafe_allow_html=True)
            
            for source, value in list(emissions_dict.items()):
                percentage = (value / sum(emissions_dict.values())) * 100
                st.markdown(f"""
                <div class="progress-section">
                    <div style="display: flex; justify-content: space-between; align-items: center;">
                        <span style="font-weight: 600;">{source}</span>
                        <span style="color: #10b981; font-weight: 700;">{percentage:.0f}%</span>
                    </div>
                </div>
                """, unsafe_allow_html=True)
    
    with tab3:
        categories = ['Energy', 'Transport', 'Diet', 'Waste', 'Water']
        values = [
            df['electricity_emission'].sum(),
            df['vehicle_emission'].sum(),
            df['diet_emission'].sum(),
            df['waste_emission'].sum(),
            df['water_emission'].sum()
        ]
        st.plotly_chart(create_comparison_chart(categories, values), use_container_width=True, height=450)

def show_calculator(df):
    """Calculator page with detailed tracking."""
    
    st.markdown('<h2 class="section-title">Calculate Your Footprint</h2>', unsafe_allow_html=True)
    
    st.markdown("""
    <div class="progress-section">
        <p style="margin: 0; color: #cbd5e1;">Answer questions about your daily lifestyle to get your personalized carbon footprint and recommendations.</p>
    </div>
    """, unsafe_allow_html=True)
    
    with st.form("calculator_form"):
        col1, col2 = st.columns(2, gap="large")
        
        with col1:
            st.markdown('<p class="card-title"> Home Energy</p>', unsafe_allow_html=True)
            electricity = st.slider("Daily electricity usage (kWh)", 0, 50, 12, help="Average US household uses ~30 kWh/day")
            gas = st.slider("Weekly natural gas (m)", 0.0, 10.0, 2.0, help="If you have gas heating/cooking")
            water = st.slider("Daily water usage (liters)", 0, 500, 100, help="Average person uses 80-100L/day")
        
        with col2:
            st.markdown('<p class="card-title"> Transportation</p>', unsafe_allow_html=True)
            vehicle_km = st.slider("Weekly vehicle distance (km)", 0, 500, 100, help="Personal car driving")
            transit_km = st.slider("Weekly public transit (km)", 0, 300, 50, help="Bus, train, metro")
            flights = st.slider("Annual flights", 0, 20, 2, help="Number of flights per year (round trip counts as 1)")
        
        col1, col2 = st.columns(2, gap="large")
        
        with col1:
            st.markdown('<p class="card-title"> Diet & Food</p>', unsafe_allow_html=True)
            diet = st.selectbox("Your diet type", ["Omnivore", "Vegetarian", "Vegan", "Flexitarian"], help="Choose your primary diet")
            meat = st.slider("Meat portions per week", 0, 14, 5, help="Red meat, chicken, fish combined")
        
        with col2:
            st.markdown('<p class="card-title"> Lifestyle & Waste</p>', unsafe_allow_html=True)
            household = st.slider("Household size", 1, 10, 2, help="Number of people in your household")
            waste = st.slider("Weekly waste (kg)", 0, 50, 10, help="Non-recyclable waste only")
            shopping_frequency = st.select_slider("New clothes/items per month", [0, 5, 10, 15, 20, 25], value=10)
        
        st.markdown("---")
        
        if st.form_submit_button("Calculate My Footprint", use_container_width=True):
            # Calculate detailed breakdown
            electricity_emission = electricity * 0.5
            gas_emission = (gas / 7) * 2.0
            water_emission = water * 0.001
            vehicle_emission = (vehicle_km / 7) * 0.2
            transit_emission = (transit_km / 7) * 0.04
            flights_emission = flights * 5 / 365
            meat_emission = meat * 1.5 / 7
            waste_emission = (waste / 7) * 0.5
            shopping_emission = shopping_frequency * 0.15
            
            total_daily = (electricity_emission + gas_emission + water_emission + 
                          vehicle_emission + transit_emission + flights_emission + 
                          meat_emission + waste_emission + shopping_emission)
            
            # Store in session for personalization
            st.session_state.footprint = total_daily
            st.session_state.user_data = {
                'electricity': electricity,
                'gas': gas,
                'water': water,
                'vehicle': vehicle_km,
                'transit': transit_km,
                'flights': flights,
                'diet': diet,
                'meat': meat,
                'household': household,
                'waste': waste,
                'shopping': shopping_frequency,
                'electricity_emission': electricity_emission,
                'vehicle_emission': vehicle_emission,
                'diet_emission': meat_emission,
                'waste_emission': waste_emission,
                'water_emission': water_emission
            }
            
            st.success("Footprint calculated! Scroll down to see personalized recommendations.")
            
            earth_equiv = (total_daily * 365) / (5.5 * 365)
            
            col1, col2, col3 = st.columns([1.5, 1, 1])
            
            with col1:
                st.markdown(f"""
                <div class="progress-section">
                    <h4 style="margin-top: 0;">Your Daily Carbon Footprint</h4>
                    <div style="font-size: 2.8rem; margin: 1rem 0; font-weight: 800;">
                        <span class="gradient-text">{total_daily:.2f}</span>
                    </div>
                    <p style="margin: 0.5rem 0; color: #cbd5e1;">kg CO2e per day</p>
                </div>
                """, unsafe_allow_html=True)
            
            with col2:
                st.markdown(f"""
                <div class="progress-section">
                    <h4 style="margin-top: 0;">Annual Total</h4>
                    <div style="font-size: 2rem; margin: 1rem 0; font-weight: 800; color: #14b8a6;">
                        {total_daily * 365:.0f}
                    </div>
                    <p style="margin: 0; color: #cbd5e1; font-size: 0.9rem;">kg CO2e/year</p>
                </div>
                """, unsafe_allow_html=True)
            
            with col3:
                status_color = "#10b981" if earth_equiv <= 1 else "#f59e0b" if earth_equiv <= 1.5 else "#ef4444"
                status = "Sustainable" if earth_equiv <= 1 else "Moderate" if earth_equiv <= 1.5 else "High"
                st.markdown(f"""
                <div class="progress-section">
                    <h4 style="margin-top: 0;">Earths Needed</h4>
                    <div style="font-size: 2rem; margin: 1rem 0; font-weight: 800; color: {status_color};">
                        {earth_equiv:.2f}
                    </div>
                    <p style="margin: 0; color: #cbd5e1; font-size: 0.9rem;">{status}</p>
                </div>
                """, unsafe_allow_html=True)
            
            # Breakdown by category
            st.markdown("---")
            st.markdown('<h3 class="card-title">Your Emissions Breakdown</h3>', unsafe_allow_html=True)
            
            breakdown = {
                ' Home Energy': electricity_emission + gas_emission + water_emission,
                ' Transportation': vehicle_emission + transit_emission + flights_emission,
                ' Diet': meat_emission,
                ' Waste & Lifestyle': waste_emission + shopping_emission
            }
            
            breakdown_cols = st.columns(4)
            for col, (category, value) in zip(breakdown_cols, breakdown.items()):
                with col:
                    percentage = (value / total_daily) * 100
                    st.markdown(f"""
                    <div class="metric-card">
                        <div class="metric-label">{category.split()[0]}</div>
                        <div style="color: #14b8a6; font-weight: 700; font-size: 1.2rem; margin: 0.5rem 0;">{percentage:.0f}%</div>
                        <div class="metric-unit">{value:.2f} kg CO2e</div>
                    </div>
                    """, unsafe_allow_html=True)

